    <>
      <div className='instagram-page'>
        <div className='ig-header'>
          <h2>Instagram</h2>
        </div>
        <div className='discount-settings'>
          <h4>Discount Settings</h4>
        </div>

        <div className='cumulative'>
          <h6>Cumulative coupons</h6>
          <div className='cumulative-container'>
            <p>Multiple coupons per user, with max % per order</p>
            <div className='cumulative-single-disc'>
              <p>Discount %</p><input></input><p>5%</p>
            </div>
            <div className='max-disc'>
              <p>Maximum discount %</p><input></input><p>40%</p>
            </div>
          </div>

        </div>

        <div className='instant'>
          <h6>Instant coupons</h6>
          <div className='instant-container'>
            <p>Two coupons per user, with max % per order</p>
            <div className='pre-sale'>
              <p>Pre-sale coupon</p><input></input><p>25%</p>
            </div>
            <div className='post-sale'>
              <p>Future-sale coupon</p><input></input><p>10%</p>
            </div>
            <div className='max-disc'>
              <p>Maximum discount %</p><p>35%</p>
            </div>
          </div>

        </div>

      </div> 
      <div>
        {/* <p>Cumulative</p>
