import React, {useState, useContext, useEffect } from 'react';
import { GlobalContext } from '../../../context/globalState';



export const Form1 = (props) => {
  const [discountCumulative, handleChangeDiscountCu] = useState('');
  const [maxDiscountCumulative, handleChangemaxDiscountCu] = useState('');
  // const [discountInstant, handleChangeDiscountIn] = React.useState('');
  // const [maxDiscountInstant, handleChangemaxDiscountIn] = React.useState('');
  const { addSingleEvent } = useContext(GlobalContext);


  const onSubmit = e => {
    // e.preventDefault();
    // console.log(items);
    const URL = "http://localhost:3001/coupons/";

    if (value == 1) {
      fetch(URL+ '5edc16759667083c0bc33de0/disc/' + discountCumulative, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res))

      fetch(URL + '5edc16759667083c0bc33de0/max/' + maxDiscountCumulative, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res));

      let newItemCu = {}
      newItemCu.type = 'cumulative';
      newItemCu.discount = discountCumulative;
      newItemCu.maxDiscount = maxDiscountCumulative;
      addSingleEvent(newItemCu);  

      let newItemIn = {}
      newItemIn.type = 'instant';
      newItemIn.discount = discountInstant;
      newItemIn.maxDiscount = maxDiscountInstant;
      addSingleEvent(newItemIn);

    } else if (value == 0) {
      fetch(URL + '5edc168a9667083c0bc33de1/disc/' + discountInstant, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res))

      fetch(URL + '5edc168a9667083c0bc33de1/max/' + maxDiscountInstant, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res));

      let newItemCu = {}
      newItemCu.type = 'cumulative';
      newItemCu.discount = discountCumulative;
      newItemCu.maxDiscount = maxDiscountCumulative;
      addSingleEvent(newItemCu);  

      let newItemIn = {}
      newItemIn.type = 'instant';
      newItemIn.discount = discountInstant;
      newItemIn.maxDiscount = maxDiscountInstant;
      addSingleEvent(newItemIn);
    }
  }



  return (
    <form onSubmit={onSubmit}>
      <div className='cumulative-single-disc'>
        <p>Discount</p><input id='cumulative-input1' className='settings-input' type='text' name='discountCumulative' placeholder='3' value={discountCumulative} onChange={(a) => handleChangeDiscountCu(a.target.value)} ></input>
        <p>10%</p>
      </div>
      <div className='max-disc'>
        <p>Maximum discount</p><input id='cumulative-input2' className='settings-input' type='text' name='discountCumulative' placeholder='3' value={maxDiscountCumulative} onChange={(a) => handleChangemaxDiscountCu(a.target.value)} ></input>
        <p>10%</p>
        <button type="submit" value="Submit" className='ig-form-data-button'>Change</button>
      </div>
    </form>
  )
}
