import React from 'react';
import Chart from 'react-apexcharts';

import './chartbox.css';





function Chartboxes () {

  let  series = [{
    data: [25, 66, 41, 59, 25, 44, 12, 36, 9, 21]
  }];

  let options = {
    chart: {
      id: 'spark1',
      group: 'sparks',
      type: 'line',
      height: 80,
      sparkline: {
        enabled: true
      },
      dropShadow: {
        enabled: true,
        top: 1,
        left: 1,
        blur: 2,
        opacity: 0.2,
      }
    },
    stroke: {
      curve: 'smooth'
    },
    markers: {
      size: 0
    },
    grid: {
      padding: {
        top: 20,
        bottom: 10,
        left: 110
      }
    },
    colors: ['#fff'],
    tooltip: {
      x: {
        show: false
      },
      y: {
        title: {
          formatter: function formatter(val) {
            return '';
          }
        }
      }
    }
  }

  return (
  <>
    <div class="row sparkboxes mt-4">

      <div class="col-md-3">
        <div class="box box1">
          <div class="details">
            <h3>1213</h3>
            <h4>CLICKS</h4>
          </div>          
            <Chart options={options} series={series} type="line" height={80} />          
        </div>
      </div>

      <div class="col-md-3">
        <div class="box box2">
          <div class="details">
            <h3>422</h3>
            <h4>VIEWS</h4>
          </div>
            <Chart options={options} series={series} type="line" height={80} />
        </div>
      </div>

      <div class="col-md-3">
        <div class="box box3">
          <div class="details">
            <h3>311</h3>
            <h4>LEADS</h4>
          </div>
            <Chart options={options} series={series} type="line" height={80} />
        </div>
      </div>

      <div class="col-md-3">
        <div class="box box4">
          <div class="details">
            <h3>22</h3>
            <h4>SALES</h4>
          </div>
            <Chart options={options} series={series} type="line" height={80} />
        </div>
      </div>
    </div>
  </>
  );
}

export default Chartboxes;
