// import React, {useState, useContext, useEffect } from 'react';
import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import './typeDiscount.css';

import { GlobalContext } from '../../../context/globalState';
import ApiClient from '../../../context/ApiClient';

import { Discount } from './values/discount';
import { MaxDiscount } from './values/maxDiscount';
// import { Form1 } from './typeformcomponents/form1';
// import { Form2 } from './typeformcomponents/form2';



function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div role="tabpanel" hidden={value !== index} id={`simple-tabpanel-${index}`} aria-labelledby={`simple-tab-${index}`} {...other} >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
}));



















export default function SimpleTabs() {
  const { addSingleEvent } = useContext(GlobalContext);
  const { items } = useContext(GlobalContext);

  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const [discountCumulative, handleChangeDiscountCu] = React.useState('');
  const [maxDiscountCumulative, handleChangemaxDiscountCu] = React.useState('');
  const [discountInstant, handleChangeDiscountIn] = React.useState('');
  const [maxDiscountInstant, handleChangemaxDiscountIn] = React.useState('');

  useEffect(() => {
    ApiClient.getAllCoupons()
    .then(data => data.map(item => addSingleEvent(item))); // eslint-disable-next-line
  }, []);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };



  const onSubmit = e => {
    // e.preventDefault();
    // setValue(e.target.value);
    console.log(items);
    const URL = "http://localhost:3001/coupons/";

    if (value == 1) {
      fetch(URL+ '5edc16759667083c0bc33de0/disc/' + discountCumulative, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res))

      fetch(URL + '5edc16759667083c0bc33de0/max/' + maxDiscountCumulative, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res));

      let newItemCu = {}
      newItemCu.type = 'cumulative';
      newItemCu.discount = discountCumulative;
      newItemCu.maxDiscount = maxDiscountCumulative;
      addSingleEvent(newItemCu);  

      let newItemIn = {}
      newItemIn.type = 'instant';
      newItemIn.discount = discountInstant;
      newItemIn.maxDiscount = maxDiscountInstant;
      addSingleEvent(newItemIn);

    } else if (value == 0) {
      fetch(URL + '5edc168a9667083c0bc33de1/disc/' + discountInstant, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res))

      fetch(URL + '5edc168a9667083c0bc33de1/max/' + maxDiscountInstant, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res));

      let newItemCu = {}
      newItemCu.type = 'cumulative';
      newItemCu.discount = discountCumulative;
      newItemCu.maxDiscount = maxDiscountCumulative;
      addSingleEvent(newItemCu);  

      let newItemIn = {}
      newItemIn.type = 'instant';
      newItemIn.discount = discountInstant;
      newItemIn.maxDiscount = maxDiscountInstant;
      addSingleEvent(newItemIn);
    }
  }

  let cumulative = {};
  let instant = {};
  items.map (item => {
    if (item.type === 'cumulative') cumulative = item;
    else if (item.type === 'instant') instant = item;
  });

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Tabs className ='tabs' value={value} onChange={handleChange} aria-label="simple tabs example">
          <Tab className='tab' label="Cumulative discounts" {...a11yProps(0)} />
          <Tab className='tab' label="Instant discount" {...a11yProps(1)} />
        </Tabs>
      </AppBar>


      <TabPanel value={value} index={0}>

      </TabPanel>

      <TabPanel value={value} index={1}>

      </TabPanel>
    </div>
  );
}
