import React, {useState, useContext, useEffect } from 'react';
import { GlobalContext } from '../../context/globalState';
import ApiClient from '../../context/ApiClient';
import './instagram.css';

import { Discount } from './discount';
import { MaxDiscount } from './maxDiscount';


function Instagram () {
  const { items } = useContext(GlobalContext);
  const { addSingleEvent } = useContext(GlobalContext);

  useEffect(() => {
    ApiClient.getAllCoupons()
      .then(data => data.map(item => addSingleEvent(item))); // eslint-disable-next-line
  }, []);

  const [value, setValue] = useState('instantRadio');

  const [discountCumulative, handleChangeDiscountCu] = useState(0);
  const [maxDiscountCumulative, handleChangemaxDiscountCu] = useState(0);
  const [discountInstant, handleChangeDiscountIn] = useState(0);
  const [maxDiscountInstant, handleChangemaxDiscountIn] = useState(0);


  const onSubmit = e => {
  //   e.preventDefault();
    setValue(e.target.value);

  //   const URL = "http://localhost:3001/coupons/";

  //   if ()
  //           fetch(this.PORT + id + '/disc', { method: 'put' })
  //             .then(res => res.text())
  //             .then(res => console.log(res))

  //           fetch(this.PORT + id + '/max', { method: 'put' })
  //             .then(res => res.text())
  //             .then(res => console.log(res))

  //   // const newItem = {}
  //   // newItem.discount = discount;
  //   // newItem.maxDiscount = maxDiscount;

  //   // fetch(URL, 
  //   //   { headers: { 'Accept': 'application/json', 'Content-Type': 'application/json' },
  //   //     method: "post", body: JSON.stringify(newItem)
  //   //   })
  //   //     .then(res => res.text())
  //   //     .then(res => console.log(res))


  //   addSingleEvent(newItem);  
  //   // handleChangeDiscount('');
  //   // handleChangemaxDiscount('');
  }
  let cumulative = {};
  let instant = {};
  items.map (item => {
    if (item.type === 'cumulative') cumulative = item;
    else if (item.type === 'instant') instant = item;
  });

  return (
    <>
      <div className='instagram-page'>

        <div className='ig-header'>
          <h2>Instagram</h2>
        </div>

        <div className='ig-pagebody'>

          <div className='left'>
            <div className='discount-settings'>
              <h4>Discounts Campaign</h4>
            </div>

            <form className='ig-form'>
              <div className='form-data'>
                <div className='cumulative'>
                  <div className='ig-radiobuttons'><input type ='radio' id="cumulativeRadio" name="discount" value={value} onChange={(e) => setValue(e.target.value)} ></input><label for='cumulativeRadio'>Cumulative coupons</label></div>
                  <div className='cumulative-container'>
                    <p className='settings-specs'>Multiple coupons per user, with max % per user</p>
                    <p className='settings-specs'>For businesses with concurrent payments/ services <br></br> (examples: gym memberships, codeworks ISA plan)</p>
                    <div className='cumulative-single-disc'>  
                      <p>Discount %</p><input id='cumulative-input1' className='settings-input' type="number" min="1" max="100" value={discountCumulative} onChange={(e) => handleChangeDiscountCu(e.target.value)} ></input>
                      {
                        <Discount discount={cumulative.discount} /> 
                      }
                    </div>
                    <div className='max-disc'>
                      <p>Maximum discount %</p><input id='cumulative-input2' className='settings-input' type="number" min="1" max="100" value={maxDiscountCumulative} onChange={(e) => handleChangemaxDiscountCu(e.target.value)} ></input>
                      {
                        <MaxDiscount maxDiscount={cumulative.maxDiscount} /> 
                      }
                    </div>
                  </div>
                </div>

                <div className='instant'>
                  <div className='ig-radiobuttons'><input type ='radio' id="instantRadio" name="discount" value={value} onChange={(e) => setValue(e.target.value)}></input><label for='instantRadio'>Instant coupons</label></div>
                  <div className='instant-container'>
                    <p className='settings-specs'>Two coupons per user, with max % per order</p>
                    <p className='settings-specs'>For businesses with instant payments/ products <br></br> (examples: restaurants, online clothing stores)</p>
                    <div className='pre-sale'>
                      <p>Pre-sale coupon</p><input id='instant-input1' className='settings-input' type="number" min="1" max="100" value={discountInstant} onChange={(e) => handleChangeDiscountIn(e.target.value)} ></input>
                      {
                        <Discount discount={instant.discount} /> 
                      }
                    </div>
                     <div className='max-disc'>
                      <p>Maximum discount %</p><input id='instant-input2' className='settings-input' type="number" min="1" max="100" value={maxDiscountInstant} onChange={(e) => handleChangemaxDiscountIn(e.target.value)} ></input>
                      {
                        <MaxDiscount maxDiscount={instant.maxDiscount} /> 
                      }
                    </div>
                  </div>
                </div>
              </div>
                <button type="submit" value="Submit" className='ig-form-data-button'>Change</button>
            </form>
          </div>

          <form className='right'>
            <h5>Social media connection</h5>
            <div className='ig-connection'>
              <p>Username:</p><input></input><p>juanpc10</p>
            </div>
            <div className='ig-connection' id='password'>
              <p>Password:</p><input></input><p>*************</p>
            </div>
            <p className='ig-connection-info'>Data is encrypted, secured, and only available to you</p>
            <button>Change</button>
          </form>


          <SimpleTabs />

        </div>
      </div> 
      {/* {
        items.map(item => <p>{item.discount}</p>)
      } */}
    </>
  )
}

export default Instagram;
