import React, {useState, useContext, useEffect } from 'react';
import { GlobalContext } from '../../../context/globalState';




export const Form2 = (props) => {
  // const [discountCumulative, handleChangeDiscountCu] = React.useState('');
  // const [maxDiscountCumulative, handleChangemaxDiscountCu] = React.useState('');
  const [discountInstant, handleChangeDiscountIn] = useState('');
  const [maxDiscountInstant, handleChangemaxDiscountIn] = useState('');
  const { addSingleEvent } = useContext(GlobalContext);

  const onSubmit = e => {
    // e.preventDefault();
    console.log(items);
    const URL = "http://localhost:3001/coupons/";

    if (value == 1) {
      fetch(URL+ '5edc16759667083c0bc33de0/disc/' + discountCumulative, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res))

      fetch(URL + '5edc16759667083c0bc33de0/max/' + maxDiscountCumulative, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res));

      let newItemCu = {}
      newItemCu.type = 'cumulative';
      newItemCu.discount = discountCumulative;
      newItemCu.maxDiscount = maxDiscountCumulative;
      addSingleEvent(newItemCu);  

      let newItemIn = {}
      newItemIn.type = 'instant';
      newItemIn.discount = discountInstant;
      newItemIn.maxDiscount = maxDiscountInstant;
      addSingleEvent(newItemIn);

    } else if (value == 0) {
      fetch(URL + '5edc168a9667083c0bc33de1/disc/' + discountInstant, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res))

      fetch(URL + '5edc168a9667083c0bc33de1/max/' + maxDiscountInstant, { method: 'put' })
        .then(res => res.text())
        .then(res => console.log(res));

      let newItemCu = {}
      newItemCu.type = 'cumulative';
      newItemCu.discount = discountCumulative;
      newItemCu.maxDiscount = maxDiscountCumulative;
      addSingleEvent(newItemCu);  

      let newItemIn = {}
      newItemIn.type = 'instant';
      newItemIn.discount = discountInstant;
      newItemIn.maxDiscount = maxDiscountInstant;
      addSingleEvent(newItemIn);
    }
  }

  let cumulative = {};
  let instant = {};
  items.map (item => {
    if (item.type === 'cumulative') cumulative = item;
    else if (item.type === 'instant') instant = item;
  });
  return (
    <>
      <div className='pre-sale'>
            <p>Pre-sale coupon</p><input id='instant-input1' className='settings-input' type='text' name='discountInstant' placeholder='3' value={discountInstant} onChange={(a) => handleChangeDiscountIn(a.target.value)} ></input>
            <p>20%</p>
          </div>
          <div className='max-disc'>
            <p>Maximum discount</p><input id='instant-input2' className='settings-input' type='text' name='maxDiscountInstant' placeholder='3' value={maxDiscountInstant} onChange={(a) => handleChangemaxDiscountIn(a.target.value)} ></input>
            <p>20%</p>
          </div>
    </>
  )
}
