import React, { useState, useEffect } from 'react';
import './channels.css';
// import { Link } from 'react-router';

function Channels () {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetchRequest().then(data => data.map(item => setItems(item)));
  }, []);

  async function fetchRequest () {
    try {
      const res = await fetch('https://private-anon-097fe242aa-codemocracy.apiary-mock.com/topics');
      const res_1 = res.status <= 400 ? res : Promise.reject(res);
      return await res_1.json();
    }
    catch (err) {
      console.log(err);
    }
  };

  console.log(items);


  return (
    <div className='channels-page'>

      <div className='channels-header'>
        <h2>Media Reward Channels</h2>
      </div>

      <div className='media-channels-container'>
        <a href=''>
          <img src='https://www.brandemia.org/sites/default/files/inline/images/twitter_logo_colores.gif' alt='Twitter'></img>
        </a>
      </div>

      <div className='media-channels-coming-soon'>
        <div className='channels-header'>
          <h2>Coming soon</h2>
        </div>
        <a href=''>
          <img src='https://s1.eestatic.com/2019/11/28/omicrono/Internet-Redes_sociales-Tecnologia-Facebook-Instagram-Omicrono_447966574_139153578_1706x960.jpg' alt='Instagram'></img>
        </a>
        <a href=''>
          <img src='https://about.fb.com/wp-content/uploads/2018/11/fb-hero-image-001.jpeg?fit=1920%2C1080' at='Facebook'></img>
        </a>
        <a href=''>
          <img src='https://static.facebook.com/images/whatsapp/www/whatsapp-promo.png' alt='whatsapp'></img>
        </a>
        <a href=''>
          <img src='https://coiski.com/staging/wp-content/uploads/2018/01/Coiski.jpg' alt='snapchat'></img>
        </a>
        <a href=''>
          <img src='https://e00-marca.uecdn.es/assets/multimedia/imagenes/2020/01/28/15802459449976.jpg' alt='tik-tok'></img>
        </a>
        <a href=''>
          <img src='https://tentulogo.com/wp-content/uploads/Pinterest-CABECERA-POST-BLOG.jpg' at='pinterest'></img>
        </a>
        <a href=''>
          <img src='https://cdn.shortpixel.ai/client/q_glossy,ret_img,w_950/https://www.irishwebservices.ie/wp-content/uploads/2019/11/google-my-business-chattanooga-marketing-company.jpg' alt='google-my-business'></img>
        </a>
        <a href=''>
          <img src='https://cdn.vox-cdn.com/thumbor/Ey90ZIOiuYjH66Yl0UeNocJjUTo=/0x0:1000x640/1200x800/filters:focal(420x240:580x400)/cdn.vox-cdn.com/uploads/chorus_image/image/56041811/Yelp_trademark_RGB_outline.0.jpg' at='yelp'></img>
        </a>
      </div>

    </div>
  )
}

export default Channels;
