import React, {useState, useContext, useEffect } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import { GlobalContext } from '../../../../context/globalState';
import ApiClient from '../../../../context/ApiClient';



const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 300,
  },
});

export default function MediaCard() {
  const { addSingleEvent } = useContext(GlobalContext);
  const { items } = useContext(GlobalContext);

  const classes = useStyles();

  useEffect(() => {
    ApiClient.getAllCoupons()
    .then(data => data.map(item => addSingleEvent(item))); // eslint-disable-next-line

  }, []);

  let currentItemType = {};
  let imgString;
  let cardHeader;
  let descriptionString;
  let discNumber;
  let maxDiscNumber; 
  items.map (item => {
    if (item.type === 'cumulative')  {
      if (item.active === true) currentItemType = item;
      imgString = 'https://www.sportoutdoor24.it/app/uploads/2016/12/troppi-selfie-fitness-problema-psicologico-768x539.jpg'
      cardHeader = 'Cumulative Coupons';
      descriptionString = 'Multiple coupons per user, with max % per user. For businesses with recurring payments/ services (e.g. gym memberships, online classes)';
      discNumber = '1%';
      maxDiscNumber = '32%';


    } else if (item.type === 'instant') {
      if (item.active === true) currentItemType = item;
    }
  });


  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={imgString}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {currentItemType.type}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {descriptionString}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="secondary">
          {discNumber}
        </Button>
        <Button size="small" color="secondary">
          {maxDiscNumber}
        </Button>
      </CardActions>
    </Card>
  );
}
