import React from 'react';
import Chart from 'react-apexcharts';






function Chartboxes () {
  window.Apex = {
    chart: {
      foreColor: '#ccc',
      toolbar: {
        show: false
      },
    },
    stroke: {
      width: 3
    },
    dataLabels: {
      enabled: false
    },
    tooltip: {
      theme: 'dark'
    },
    grid: {
      borderColor: "#535A6C",
      xaxis: {
        lines: {
          show: true
        }
      }
    }
  };

  let  series = [{
    data: [25, 66, 41, 59, 25, 44, 12, 36, 9, 21]
  }];

  let options = {
    chart: {
      id: 'spark1',
      group: 'sparks',
      type: 'line',
      height: 80,
      sparkline: {
        enabled: true
      },
      dropShadow: {
        enabled: true,
        top: 1,
        left: 1,
        blur: 2,
        opacity: 0.2,
      }
    },
    stroke: {
      curve: 'smooth'
    },
    markers: {
      size: 0
    },
    grid: {
      padding: {
        top: 20,
        bottom: 10,
        left: 110
      }
    },
    colors: ['#fff'],
    tooltip: {
      x: {
        show: false
      },
      y: {
        title: {
          formatter: function formatter(val) {
            return '';
          }
        }
      }
    }
  }

  return (
    <div class="row sparkboxes mt-4">
      <div class="col-md-3">
        <div class="box box1">
          <div class="details">
            <h3>1213</h3>
            <h4>CLICKS</h4>
          </div>
          <div id="spark1">
            <Chart options={options} series={series} type="line" height={80} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chartboxes;
