import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import './typeDiscount.css';

import { GlobalContext } from '../../context/globalState';
import ApiClient from '../../context/ApiClient';

import { Discount } from './forms/values/discount';
import { MaxDiscount } from './forms/values/maxDiscount';


function TabPanel(props) {
  const { children, value, index, ...other } = props;

  const { items } = useContext(GlobalContext);
  const { addSingleEvent } = useContext(GlobalContext);

  useEffect(() => {
    ApiClient.getAllCoupons()
      .then(data => data.map(item => addSingleEvent(item))); // eslint-disable-next-line
  }, []);

  // const [value, setValue] = useState('instantRadio');

  const [discountCumulative, handleChangeDiscountCu] = useState(0);
  const [maxDiscountCumulative, handleChangemaxDiscountCu] = useState(0);
  const [discountInstant, handleChangeDiscountIn] = useState(0);
  const [maxDiscountInstant, handleChangemaxDiscountIn] = useState(0);


  const onSubmit = e => {
  //   e.preventDefault();
    setValue(e.target.value);

  //   const URL = "http://localhost:3001/coupons/";

  //   if ()
  //           fetch(this.PORT + id + '/disc', { method: 'put' })
  //             .then(res => res.text())
  //             .then(res => console.log(res))

  //           fetch(this.PORT + id + '/max', { method: 'put' })
  //             .then(res => res.text())
  //             .then(res => console.log(res))

  //   // const newItem = {}
  //   // newItem.discount = discount;
  //   // newItem.maxDiscount = maxDiscount;

  //   // fetch(URL, 
  //   //   { headers: { 'Accept': 'application/json', 'Content-Type': 'application/json' },
  //   //     method: "post", body: JSON.stringify(newItem)
  //   //   })
  //   //     .then(res => res.text())
  //   //     .then(res => console.log(res))


  //   addSingleEvent(newItem);  
  //   // handleChangeDiscount('');
  //   // handleChangemaxDiscount('');
  }
  let cumulative = {};
  let instant = {};
  items.map (item => {
    if (item.type === 'cumulative') cumulative = item;
    else if (item.type === 'instant') instant = item;
  });


  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
}));

export default function SimpleTabs() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Tabs className ='tabs' value={value} onChange={handleChange} aria-label="simple tabs example">
          <Tab className='tab' label="Cumulative discounts" {...a11yProps(0)} />
          <Tab className='tab' label="Instant discount" {...a11yProps(1)} />
        </Tabs>
      </AppBar>
      <TabPanel value={value} index={0}>
        <div className='cumulative-single-disc'>  
          <p>Discount %</p><input id='cumulative-input1' className='settings-input' type="number" min="1" max="100" value={discountCumulative} onChange={(e) => handleChangeDiscountCu(e.target.value)} ></input>
          {
            <Discount discount={cumulative.discount} /> 
          }
        </div>
        <div className='max-disc'>
          <p>Maximum discount %</p><input id='cumulative-input2' className='settings-input' type="number" min="1" max="100" value={maxDiscountCumulative} onChange={(e) => handleChangemaxDiscountCu(e.target.value)} ></input>
          {
            <MaxDiscount maxDiscount={cumulative.maxDiscount} /> 
          }
        </div>
        Cumulative coupons
      </TabPanel>
      <TabPanel value={value} index={1}>
        Instant Coupons
      </TabPanel>
    </div>
  );
}
